import React from 'react';

const StartScreen = ({ onStartGame }) => {
  const styles = {
    container: {
      width: '100vw',
      height: '100vh',
      backgroundImage: 'url(/backgrounds/tileable-background-skyline.png)',
      backgroundSize: 'auto 100%',
      backgroundPosition: 'left center',
      backgroundRepeat: 'repeat-x',
      position: 'relative',
      margin: 0,
      padding: 0,
      overflow: 'hidden'
    },
    bamboo: {
      position: 'absolute',
      left: '20px',
      bottom: '0',
      width: '80px',
      height: 'auto',
      zIndex: 5
    },
    cherryBlossoms: {
      position: 'absolute',
      right: '20px',
      top: '20px',
      width: '120px',
      height: 'auto',
      zIndex: 5
    },
    titleContainer: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      textAlign: 'center',
      zIndex: 10
    },
    superIshitaLogo: {
      width: '400px',
      height: 'auto',
      marginBottom: '20px'
    },
    shanghaiAdventureLogo: {
      width: '300px',
      height: 'auto',
      marginBottom: '40px'
    },
    startButton: {
      padding: '20px 40px',
      fontSize: '24px',
      backgroundColor: '#ff4444',
      color: 'white',
      border: 'none',
      borderRadius: '10px',
      cursor: 'pointer',
      fontFamily: 'monospace',
      fontWeight: 'bold',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
      transition: 'transform 0.2s ease-in-out',
      zIndex: 15
    }
  };

  return (
    <div style={styles.container}>
      {/* Bamboo on the left */}
      <img 
        src="/assets/sprites/bamboo.png" 
        alt="Bamboo" 
        style={styles.bamboo}
      />

      {/* Cherry blossoms on the right */}
      <img 
        src="/assets/sprites/cherryblossom-floor.png" 
        alt="Cherry Blossoms" 
        style={styles.cherryBlossoms}
      />

      {/* Title container */}
      <div style={styles.titleContainer}>
        {/* SUPER ISHITA BROs logo */}
        <img 
          src="/assets/sprites/super-ishita.png" 
          alt="SUPER ISHITA BROs" 
          style={styles.superIshitaLogo}
        />

        {/* SHANGHAI ADVENTURE logo */}
        <img 
          src="/assets/sprites/shanghai-adventure.png" 
          alt="SHANGHAI ADVENTURE" 
          style={styles.shanghaiAdventureLogo}
        />

        {/* Start button */}
        <button 
          style={styles.startButton}
          onClick={onStartGame}
          onMouseEnter={(e) => {
            e.target.style.transform = 'scale(1.05)';
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'scale(1)';
          }}
        >
          START GAME
        </button>
      </div>
    </div>
  );
};

export default StartScreen;
