import React from 'react';

const StoryPanel = ({ story, onClose }) => {
  const styles = {
    overlay: {
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      background: 'rgba(0,0,0,0.7)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 100
    },
    panel: {
      background: '#000',
      border: '8px solid #FFF',
      padding: '32px',
      maxWidth: '600px',
      margin: '20px',
      position: 'relative',
      fontFamily: 'Press Start 2P, cursive',
      color: 'white',
      lineHeight: '1.8'
    },
    closeButton: {
      position: 'absolute',
      top: '8px',
      right: '12px',
      background: 'none',
      border: 'none',
      fontSize: '1.2rem',
      cursor: 'pointer',
      color: 'white',
      fontFamily: 'Press Start 2P, cursive'
    },
    title: {
      fontSize: '1.2rem',
      marginBottom: '20px',
      color: '#FFFF00',
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      textShadow: '2px 2px 0px #000'
    },
    text: {
      color: 'white',
      lineHeight: '1.8',
      fontSize: '0.9rem',
      letterSpacing: '1px'
    },
    continuePrompt: {
      marginTop: '20px',
      fontSize: '0.8rem',
      color: '#FFFF00',
      textAlign: 'right',
      animation: 'blink 1s infinite'
    }
  };

  return (
    <>
      <style>
        {`
          @keyframes blink {
            0%, 50% { opacity: 1; }
            51%, 100% { opacity: 0; }
          }
        `}
      </style>
      <div style={styles.overlay} onClick={onClose}>
        <div style={styles.panel} onClick={(e) => e.stopPropagation()}>
          <button style={styles.closeButton} onClick={onClose}>Ã—</button>
          <h3 style={styles.title}>
            <span style={{ fontSize: '2rem' }}>{story.icon}</span>
            {story.title}
          </h3>
          <p style={styles.text}>
            {story.text}
          </p>
          <div style={styles.continuePrompt}>
            PRESS X TO CONTINUE
          </div>
        </div>
      </div>
    </>
  );
};

export default StoryPanel;
