import React from 'react';

const EndScreen = ({ onRestartGame }) => {
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      background: '#000',
      textAlign: 'center',
      color: 'white',
      fontFamily: 'Press Start 2P, cursive',
      position: 'relative'
    },
    castle: {
      position: 'absolute',
      top: '20%',
      fontSize: '8rem',
      animation: 'glow 2s ease-in-out infinite alternate'
    },
    title: {
      fontSize: '2rem',
      marginBottom: '2rem',
      color: '#FFFF00',
      textShadow: '4px 4px 0px #000',
      letterSpacing: '3px'
    },
    subtitle: {
      fontSize: '1.2rem',
      marginBottom: '1rem',
      color: 'white'
    },
    message: {
      fontSize: '1rem',
      marginBottom: '3rem',
      color: '#00FF00',
      lineHeight: '1.8'
    },
    scoreBox: {
      border: '4px solid white',
      padding: '20px 40px',
      marginBottom: '2rem',
      background: '#000'
    },
    score: {
      fontSize: '1.5rem',
      color: '#FFFF00',
      marginBottom: '10px'
    },
    button: {
      padding: '15px 30px',
      fontSize: '1rem',
      background: 'transparent',
      color: '#FFFF00',
      border: '4px solid #FFFF00',
      cursor: 'pointer',
      fontFamily: 'Press Start 2P, cursive',
      transition: 'all 0.2s ease'
    },
    fireworks: {
      position: 'absolute',
      fontSize: '3rem',
      animation: 'sparkle 1.5s ease-in-out infinite'
    }
  };

  return (
    <>
      <style>
        {`
          @keyframes glow {
            0% { text-shadow: 0 0 20px #FFD700; }
            100% { text-shadow: 0 0 30px #FFD700, 0 0 40px #FFD700; }
          }
          @keyframes sparkle {
            0%, 100% { transform: scale(1) rotate(0deg); opacity: 1; }
            50% { transform: scale(1.2) rotate(180deg); opacity: 0.7; }
          }
        `}
      </style>
      <div style={styles.container}>
        {/* Fireworks */}
        <div style={{...styles.fireworks, top: '15%', left: '10%', animationDelay: '0s'}}>‚ú®</div>
        <div style={{...styles.fireworks, top: '25%', right: '15%', animationDelay: '0.5s'}}>‚ú®</div>
        <div style={{...styles.fireworks, top: '35%', left: '20%', animationDelay: '1s'}}>‚ú®</div>
        <div style={{...styles.fireworks, top: '45%', right: '25%', animationDelay: '1.5s'}}>‚ú®</div>

        {/* Castle */}
        <div style={styles.castle}>üè∞</div>

        <h1 style={styles.title}>CONGRATULATIONS!</h1>

        <div style={styles.scoreBox}>
          <div style={styles.score}>YOUR SCORE</div>
          <div style={styles.score}>050000</div>
        </div>

        <p style={styles.subtitle}>
          THANK YOU MARIO!
        </p>
        <p style={styles.message}>
          YOUR QUEST IS OVER.<br/>
          PRINCESS PEACH HAS BEEN SAVED!<br/>
          THE MUSHROOM KINGDOM IS SAFE!
        </p>

        <button 
          style={styles.button} 
          onClick={onRestartGame}
          onMouseOver={(e) => {
            e.target.style.background = '#FFFF00';
            e.target.style.color = '#000';
          }}
          onMouseOut={(e) => {
            e.target.style.background = 'transparent';
            e.target.style.color = '#FFFF00';
          }}
        >
          PLAY AGAIN
        </button>
      </div>
    </>
  );
};

export default EndScreen;
