import React from 'react';

const EndScreen = ({ onRestartGame }) => {
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      background: 'linear-gradient(45deg, #FF6B6B, #4ECDC4)',
      textAlign: 'center',
      color: 'white'
    },
    title: {
      fontSize: '3rem',
      marginBottom: '1rem',
      textShadow: '2px 2px 0px #333'
    },
    subtitle: {
      fontSize: '1.5rem',
      marginBottom: '1rem'
    },
    message: {
      fontSize: '1.2rem',
      marginBottom: '2rem',
      opacity: 0.9
    },
    button: {
      padding: '1rem 2rem',
      fontSize: '1.2rem',
      background: '#4ECDC4',
      color: 'white',
      border: 'none',
      borderRadius: '25px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      boxShadow: '0 4px 15px rgba(0,0,0,0.2)'
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>ðŸŽ‰ Congratulations! ðŸŽ‰</h1>
      <p style={styles.subtitle}>
        You've completed the adventure!
      </p>
      <p style={styles.message}>
        Princess Peach and the Mushroom Kingdom are safe!
      </p>
      <button 
        style={styles.button} 
        onClick={onRestartGame}
        onMouseOver={(e) => {
          e.target.style.background = '#45B7D1';
          e.target.style.transform = 'translateY(-2px)';
        }}
        onMouseOut={(e) => {
          e.target.style.background = '#4ECDC4';
          e.target.style.transform = 'translateY(0)';
        }}
      >
        Play Again
      </button>
    </div>
  );
};

export default EndScreen;
