import React, { useState, useEffect } from 'react';

const StartScreen = ({ onStartGame }) => {
  const [clouds, setClouds] = useState([]);

  // Generate a random cloud
  const generateCloud = () => {
    const cloudTypes = ['/assets/sprites/cloud.png', '/assets/sprites/different-cloud.png'];
    const cloudType = cloudTypes[Math.floor(Math.random() * cloudTypes.length)];

    const newCloud = {
      id: Date.now() + Math.random(),
      src: cloudType,
      top: Math.random() * 150 + 20, // Random position between 20-170px from top
      size: Math.random() * 60 + 40, // Random size between 40-100px
      duration: Math.random() * 15 + 10, // Random duration between 10-25s
      delay: Math.random() * 5 // Random delay between 0-5s
    };

    setClouds(prev => [...prev, newCloud]);

    // Remove cloud after animation completes
    setTimeout(() => {
      setClouds(prev => prev.filter(cloud => cloud.id !== newCloud.id));
    }, (newCloud.duration + newCloud.delay) * 1000);
  };

  // Generate clouds at random intervals
  useEffect(() => {
    const generateRandomCloud = () => {
      generateCloud();
      // Schedule next cloud at random interval (2-8 seconds)
      const nextInterval = Math.random() * 6000 + 2000;
      setTimeout(generateRandomCloud, nextInterval);
    };

    // Start generating clouds after initial delay
    const initialDelay = Math.random() * 3000 + 1000;
    setTimeout(generateRandomCloud, initialDelay);

    return () => {
      // Cleanup will be handled by component unmount
    };
  }, []);

  const styles = {
    container: {
      width: '100vw',
      height: '100vh',
      backgroundImage: 'url(/backgrounds/tileable-background-skyline.png)',
      backgroundSize: 'auto 100%',
      backgroundPosition: 'left center',
      backgroundRepeat: 'repeat-x',
      position: 'relative',
      margin: 0,
      padding: 0,
      overflow: 'hidden',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    bamboo: {
      position: 'absolute',
      left: '50px',
      bottom: '100px',
      width: '100px',
      height: 'auto',
      zIndex: 5,
      animation: 'sway 5s ease-in-out infinite'
    },
    cherryBlossomsTop: {
      position: 'absolute',
      right: '0px',
      top: '30px',
      width: '200px',
      height: 'auto',
      zIndex: 5,
      animation: 'sway 3s ease-in-out infinite'
    },
    lantern: {
      position: 'absolute',
      left: '300px',
      top: '0px',
      width: '100px',
      height: 'auto',
      zIndex: 5,
      animation: 'sway 4s ease-in-out infinite reverse',
      filter: 'drop-shadow(0 0 8px rgba(255, 0, 0, 0.6))'
    },
    cloud: {
      position: 'absolute',
      left: '-100px',
      zIndex: 3,
      animation: 'floatCloud linear infinite'
    },
    titleContainer: {
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      textAlign: 'center',
      zIndex: 10,
      position: 'relative'
    },
    superIshitaLogo: {
      width: '800px',
      height: 'auto',
      marginBottom: '15px',
      animation: 'bounceGlow 2s ease-in-out infinite',
      filter: 'drop-shadow(0 0 10px rgba(255, 255, 255, 0.3))'
    },
    shanghaiAdventureLogo: {
      width: '300px',
      height: 'auto',
      marginBottom: '30px',
      filter: 'drop-shadow(0 0 12px rgba(255, 255, 0, 0.8))',
      animation: 'glow 3s ease-in-out infinite'
    },
    startButton: {
      padding: '15px 30px',
      fontSize: '20px',
      backgroundColor: '#ff4444',
      color: 'white',
      border: 'none',
      borderRadius: '8px',
      cursor: 'pointer',
      fontFamily: 'monospace',
      fontWeight: 'bold',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)',
      transition: 'transform 0.2s ease-in-out',
      zIndex: 15
    }
  };

  // Add CSS animations to the document
  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes bounceGlow {
        0%, 100% {
          transform: translateY(0px);
          filter: drop-shadow(0 0 10px rgba(255, 255, 255, 0.3));
        }
        50% {
          transform: translateY(-10px);
          filter: drop-shadow(0 0 20px rgba(255, 255, 255, 0.6));
        }
      }

      @keyframes sway {
        0%, 100% {
          transform: rotate(-2deg);
        }
        50% {
          transform: rotate(2deg);
        }
      }

      @keyframes floatCloud {
        0% {
          transform: translateX(-100px);
        }
        100% {
          transform: translateX(calc(100vw + 100px));
        }
      }
    `;
    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return (
    <div style={styles.container}>
      {/* Randomly generated floating clouds */}
      {clouds.map(cloud => (
        <img 
          key={cloud.id}
          src={cloud.src}
          alt="Cloud"
          style={{
            ...styles.cloud,
            top: `${cloud.top}px`,
            width: `${cloud.size}px`,
            height: 'auto',
            animation: `floatCloud ${cloud.duration}s linear infinite`,
            animationDelay: `${cloud.delay}s`
          }}
        />
      ))}

      {/* Bamboo on the left */}
      <img 
        src="/assets/sprites/bamboo.png" 
        alt="Bamboo" 
        style={styles.bamboo}
      />

      {/* Lantern on the left top */}
      <img 
        src="/assets/sprites/lantern.png" 
        alt="Lantern" 
        style={styles.lantern}
      />

      {/* Cherry blossoms on the top right */}
      <img 
        src="/assets/sprites/cherryblossom-sky.png" 
        alt="Cherry Blossoms Top" 
        style={styles.cherryBlossomsTop}
      />

      {/* Title container */}
      <div style={styles.titleContainer}>
        {/* SUPER ISHITA BROs logo */}
        <img 
          src="/assets/sprites/super-ishita.png" 
          alt="SUPER ISHITA BROs" 
          style={styles.superIshitaLogo}
        />

        {/* SHANGHAI ADVENTURE logo */}
        <img 
          src="/assets/sprites/shanghai-adventure.png" 
          alt="SHANGHAI ADVENTURE" 
          style={styles.shanghaiAdventureLogo}
        />

        {/* Start button */}
        <button 
          style={styles.startButton}
          onClick={onStartGame}
          onMouseEnter={(e) => {
            e.target.style.transform = 'scale(1.05)';
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'scale(1)';
          }}
        >
          START GAME
        </button>
      </div>
    </div>
  );
};

export default StartScreen;
