import React, { useState, useEffect } from 'react';
import StartScreen from './components/StartScreen';
import GameScreen from './components/GameScreen';

const App = () => {
  const [currentScreen, setCurrentScreen] = useState('start');
  const [clouds, setClouds] = useState([]);

  // Generate a random cloud
  const generateCloud = () => {
    const cloudTypes = ['/assets/sprites/cloud.png', '/assets/sprites/different-cloud.png'];
    const cloudType = cloudTypes[Math.floor(Math.random() * cloudTypes.length)];

    const newCloud = {
      id: Date.now() + Math.random(),
      src: cloudType,
      top: Math.random() * 150 + 20,
      size: Math.random() * 60 + 40,
      duration: Math.random() * 15 + 10,
      delay: Math.random() * 5
    };

    setClouds(prev => [...prev, newCloud]);

    setTimeout(() => {
      setClouds(prev => prev.filter(cloud => cloud.id !== newCloud.id));
    }, (newCloud.duration + newCloud.delay) * 1000);
  };

  // Generate clouds at random intervals - only when in game mode
  useEffect(() => {
    if (currentScreen === 'game') {
      const generateRandomCloud = () => {
        generateCloud();
        const nextInterval = Math.random() * 6000 + 2000;
        setTimeout(generateRandomCloud, nextInterval);
      };

      const initialDelay = Math.random() * 3000 + 1000;
      setTimeout(generateRandomCloud, initialDelay);
    }
  }, [currentScreen]);

  const goToGame = () => {
    setCurrentScreen('game');
  };

  return (
    <div style={{ position: 'relative', width: '100vw', height: '100vh' }}>
      {/* Always render the background without clouds */}
      <StartScreen onStartGame={goToGame} showUI={false} clouds={[]} />

      {/* Overlay the UI only when on start screen */}
      {currentScreen === 'start' && (
        <div style={{ 
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          zIndex: 20,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}>
          <StartScreen onStartGame={goToGame} showUI={true} clouds={[]} />
        </div>
      )}

      {/* Show clouds only during gameplay */}
      {currentScreen === 'game' && (
        <div style={{ 
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          zIndex: 3,
          pointerEvents: 'none'
        }}>
          {clouds.map(cloud => (
            <img 
              key={cloud.id}
              src={cloud.src}
              alt="Cloud"
              style={{
                position: 'absolute',
                left: '-100px',
                top: `${cloud.top}px`,
                width: `${cloud.size}px`,
                height: 'auto',
                zIndex: 3,
                animation: `floatCloud ${cloud.duration}s linear infinite`,
                animationDelay: `${cloud.delay}s`
              }}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
