import React from 'react';

const StoryPanel = ({ story, onClose }) => {
  const styles = {
    overlay: {
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      height: '100%',
      background: 'rgba(0,0,0,0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 100
    },
    panel: {
      background: 'white',
      borderRadius: '20px',
      padding: '2rem',
      maxWidth: '400px',
      margin: '20px',
      boxShadow: '0 10px 30px rgba(0,0,0,0.3)',
      position: 'relative',
      animation: 'popIn 0.3s ease'
    },
    closeButton: {
      position: 'absolute',
      top: '10px',
      right: '15px',
      background: 'none',
      border: 'none',
      fontSize: '1.5rem',
      cursor: 'pointer',
      color: '#666'
    },
    title: {
      fontSize: '1.5rem',
      marginBottom: '1rem',
      color: '#333',
      display: 'flex',
      alignItems: 'center',
      gap: '10px'
    },
    text: {
      color: '#555',
      lineHeight: '1.6',
      fontSize: '1rem'
    }
  };

  return (
    <>
      <style>
        {`
          @keyframes popIn {
            0% { transform: scale(0); opacity: 0; }
            100% { transform: scale(1); opacity: 1; }
          }
        `}
      </style>
      <div style={styles.overlay} onClick={onClose}>
        <div style={styles.panel} onClick={(e) => e.stopPropagation()}>
          <button style={styles.closeButton} onClick={onClose}>Ã—</button>
          <h3 style={styles.title}>
            <span style={{ fontSize: '2rem' }}>{story.icon}</span>
            {story.title}
          </h3>
          <p style={styles.text}>
            {story.text}
          </p>
        </div>
      </div>
    </>
  );
};

export default StoryPanel;
