import React, { useState } from 'react';
import StoryPanel from './StoryPanel';

const GameMap = ({ onEndGame }) => {
  const [completedStories, setCompletedStories] = useState([]);
  const [selectedStory, setSelectedStory] = useState(null);

  const storyItems = [
    {
      id: 1,
      position: { x: 200, y: 300 },
      icon: '🍄',
      title: 'The Beginning',
      text: "Welcome to the Mushroom Kingdom! Princess Peach needs your help to save the kingdom from Bowser's evil plans."
    },
    {
      id: 2,
      position: { x: 400, y: 250 },
      icon: '⭐',
      title: 'Power Star',
      text: "You found a Power Star! These magical stars will give you the strength to face greater challenges ahead."
    },
    {
      id: 3,
      position: { x: 600, y: 200 },
      icon: '🏰',
      title: 'Castle Mystery',
      text: "You've reached the first castle! But Princess Peach is in another castle. Keep going, Mario!"
    },
    {
      id: 4,
      position: { x: 800, y: 280 },
      icon: '🔥',
      title: 'Fire Power',
      text: "Watch out for the Fire Flowers! They can help you defeat enemies, but be careful not to get burned."
    },
    {
      id: 5,
      position: { x: 1000, y: 220 },
      icon: '👑',
      title: 'Victory!',
      text: "Congratulations! You've found Princess Peach! The kingdom is safe once again thanks to your bravery!"
    }
  ];

  const handleStoryClick = (story) => {
    setSelectedStory(story);
    if (!completedStories.includes(story.id)) {
      setCompletedStories([...completedStories, story.id]);
    }

    // End game when all stories are completed
    if (completedStories.length + 1 === storyItems.length) {
      setTimeout(() => onEndGame(), 2000);
    }
  };

  const closeStoryPanel = () => {
    setSelectedStory(null);
  };

  const styles = {
    container: {
      width: '100vw',
      height: '100vh',
      background: '#5C94FC',
      position: 'relative',
      overflow: 'hidden',
      fontFamily: 'Press Start 2P, cursive'
    },
    topHud: {
      position: 'fixed',
      top: '20px',
      width: '100%',
      display: 'flex',
      justifyContent: 'space-between',
      padding: '0 40px',
      fontSize: '1rem',
      color: 'white',
      textShadow: '2px 2px 0px #000',
      fontFamily: 'Press Start 2P, cursive',
      zIndex: 50
    },
    clouds: {
      position: 'absolute',
      width: '80px',
      height: '40px',
      background: 'white',
      borderRadius: '40px',
      opacity: 0.9
    },
    cloudBefore: {
      content: '',
      position: 'absolute',
      width: '40px',
      height: '40px',
      background: 'white',
      borderRadius: '50px',
      top: '-20px',
      left: '10px'
    },
    cloudAfter: {
      content: '',
      position: 'absolute',
      width: '50px',
      height: '30px',
      background: 'white',
      borderRadius: '30px',
      top: '-10px',
      right: '10px'
    },
    storyItem: {
      position: 'absolute',
      width: '32px',
      height: '32px',
      background: '#FFD700',
      border: '2px solid #000',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '20px',
      transition: 'all 0.1s ease',
      imageRendering: 'pixelated'
    },
    ground: {
      position: 'absolute',
      bottom: 0,
      width: '100%',
      height: '120px',
      background: '#D2691E',
      borderTop: '8px solid #000'
    },
    groundPattern: {
      width: '100%',
      height: '32px',
      background: 'repeating-linear-gradient(90deg, #D2691E 0px, #D2691E 32px, #8B4513 32px, #8B4513 64px)',
      marginTop: '8px'
    },
    pipe: {
      position: 'absolute',
      width: '64px',
      height: '96px',
      background: '#00AA00',
      border: '4px solid #000',
      bottom: '120px'
    },
    pipeTop: {
      position: 'absolute',
      top: '-8px',
      left: '-8px',
      width: '80px',
      height: '16px',
      background: '#00AA00',
      border: '4px solid #000'
    }
  };

  return (
    <div style={styles.container}>
      {/* Top HUD */}
      <div style={styles.topHud}>
        <div>MARIO<br />000000</div>
        <div>🍄×{completedStories.length.toString().padStart(2, '0')}</div>
        <div>WORLD<br />1-1</div>
        <div>TIME<br />400</div>
      </div>

      {/* Clouds */}
      <div style={{...styles.clouds, top: '100px', left: '150px'}}>
        <div style={styles.cloudBefore}></div>
        <div style={styles.cloudAfter}></div>
      </div>
      <div style={{...styles.clouds, top: '150px', left: '400px'}}>
        <div style={styles.cloudBefore}></div>
        <div style={styles.cloudAfter}></div>
      </div>
      <div style={{...styles.clouds, top: '120px', left: '700px'}}>
        <div style={styles.cloudBefore}></div>
        <div style={styles.cloudAfter}></div>
      </div>

      {/* Story Items (Mario collectibles) */}
      {storyItems.map(story => (
        <div
          key={story.id}
          style={{
            ...styles.storyItem,
            left: `${story.position.x}px`,
            bottom: `${200 + (story.id % 2) * 40}px`,
            opacity: completedStories.includes(story.id) ? 0.3 : 1,
            background: completedStories.includes(story.id) ? '#666' : '#FFD700'
          }}
          onClick={() => handleStoryClick(story)}
        >
          {story.icon}
        </div>
      ))}

      {/* Pipes */}
      <div style={{...styles.pipe, left: '100px'}}>
        <div style={styles.pipeTop}></div>
      </div>
      <div style={{...styles.pipe, left: '900px', height: '128px'}}>
        <div style={styles.pipeTop}></div>
      </div>

      {/* Story Modal */}
      {selectedStory && (
        <StoryPanel 
          story={selectedStory} 
          onClose={closeStoryPanel} 
        />
      )}

      {/* Ground */}
      <div style={styles.ground}>
        <div style={styles.groundPattern}></div>
        <div style={styles.groundPattern}></div>
        <div style={styles.groundPattern}></div>
      </div>
    </div>
  );
};

export default GameMap;
