import React from 'react';

const StartScreen = ({ onStartGame }) => {
  const styles = {
    container: {
      width: '100vw',
      height: '100vh',
      position: 'relative',
      overflow: 'hidden',
      fontFamily: 'monospace'
    },
    sky: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '85%',
      backgroundImage: 'url(/assets/sprites/cherryblossom-sky.png)',
      backgroundRepeat: 'repeat',
      backgroundSize: 'auto'
    },
    ground: {
      position: 'absolute',
      bottom: 0,
      left: 0,
      width: '100%',
      height: '15%',
      backgroundImage: 'url(/assets/sprites/cherryblossom-floor.png)',
      backgroundRepeat: 'repeat-x',
      backgroundSize: 'auto 100%'
    },
    cityscape: {
      position: 'absolute',
      bottom: '30%',
      width: '100%',
      height: '40%',
      background: 'linear-gradient(to top, rgba(0,0,0,0.3) 0%, rgba(0,0,0,0.1) 50%, transparent 100%)',
      display: 'flex',
      alignItems: 'flex-end',
      justifyContent: 'center'
    },
    building: {
      width: '60px',
      height: '120px',
      backgroundColor: 'rgba(0,0,0,0.4)',
      margin: '0 5px',
      position: 'relative'
    },
    tallBuilding: {
      width: '40px',
      height: '180px',
      backgroundColor: 'rgba(0,0,0,0.5)',
      margin: '0 3px'
    },
    clouds: {
      position: 'absolute',
      width: '80px',
      height: '40px',
      backgroundColor: 'white',
      borderRadius: '40px',
      opacity: 0.9
    },
    title: {
      fontSize: '4rem',
      color: 'white',
      textShadow: '4px 4px 0px #000',
      marginBottom: '3rem',
      fontWeight: 'bold',
      letterSpacing: '4px',
      textAlign: 'center',
      zIndex: 10
    },
    character: {
      position: 'absolute',
      bottom: '30%',
      left: '20%',
      fontSize: '4rem',
      zIndex: 5
    },
    hearts: {
      position: 'absolute',
      fontSize: '2rem',
      color: '#ff4757',
      zIndex: 3
    },
    bamboo: {
      position: 'absolute',
      left: '5%',
      bottom: '30%',
      width: '20px',
      height: '200px',
      backgroundColor: '#228B22',
      borderRadius: '10px'
    },
    flowers: {
      position: 'absolute',
      right: '10%',
      bottom: '35%',
      fontSize: '1.5rem'
    },
    startButton: {
      fontSize: '1.8rem',
      padding: '15px 40px',
      backgroundColor: '#ff6b6b',
      color: 'white',
      border: '4px solid #fff',
      borderRadius: '0',
      cursor: 'pointer',
      fontFamily: 'monospace',
      fontWeight: 'bold',
      textShadow: '2px 2px 0px #000',
      boxShadow: '4px 4px 0px #000',
      transition: 'all 0.1s ease',
      zIndex: 10
    },
    ground: {
      position: 'absolute',
      bottom: 0,
      width: '100%',
      height: '30%',
      background: 'repeating-linear-gradient(90deg, #228B22 0px, #228B22 20px, #32CD32 20px, #32CD32 40px)',
      borderTop: '4px solid #000'
    }
  };

  return (
    <div style={styles.container}>
      {/* Start Button Overlay */}
      <button 
        style={styles.startButton} 
        onClick={onStartGame}
        onMouseOver={(e) => {
          e.target.style.backgroundColor = '#ff5252';
          e.target.style.transform = 'translate(2px, 2px)';
          e.target.style.boxShadow = '2px 2px 0px #000';
        }}
        onMouseOut={(e) => {
          e.target.style.backgroundColor = '#ff6b6b';
          e.target.style.transform = 'translate(0, 0)';
          e.target.style.boxShadow = '4px 4px 0px #000';
        }}
      >
        START GAME
      </button>
    </div>
  );
};

export default StartScreen;
