import React from 'react';

const StartScreen = ({ onStartGame }) => {
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      background: '#5C94FC',
      textAlign: 'center',
      color: 'white',
      position: 'relative',
      overflow: 'hidden'
    },
    clouds: {
      position: 'absolute',
      width: '100%',
      height: '100%',
      backgroundImage: `
        radial-gradient(ellipse 60px 20px at 100px 50px, #FFFFFF 40%, transparent 50%),
        radial-gradient(ellipse 80px 30px at 300px 100px, #FFFFFF 40%, transparent 50%),
        radial-gradient(ellipse 100px 25px at 500px 80px, #FFFFFF 40%, transparent 50%)
      `,
      opacity: 0.8
    },
    titleBox: {
      background: '#FF6B00',
      border: '8px solid #000',
      borderRadius: '0',
      padding: '40px 60px',
      marginBottom: '40px',
      boxShadow: '8px 8px 0px #000'
    },
    title: {
      fontSize: '3rem',
      color: '#FFFF00',
      textShadow: '4px 4px 0px #000',
      marginBottom: '10px',
      fontFamily: 'Press Start 2P, cursive',
      letterSpacing: '4px'
    },
    subtitle: {
      fontSize: '1.5rem',
      color: '#FFFF00',
      textShadow: '2px 2px 0px #000',
      fontFamily: 'Press Start 2P, cursive',
      letterSpacing: '2px'
    },
    gameOptions: {
      display: 'flex',
      flexDirection: 'column',
      gap: '20px',
      marginBottom: '40px'
    },
    option: {
      fontSize: '1.2rem',
      color: 'white',
      textShadow: '2px 2px 0px #000',
      fontFamily: 'Press Start 2P, cursive',
      cursor: 'pointer',
      padding: '10px 20px',
      border: 'none',
      background: 'transparent',
      transition: 'all 0.2s ease'
    },
    selectedOption: {
      color: '#FFFF00'
    },
    mario: {
      fontSize: '2rem',
      marginRight: '20px'
    },
    startButton: {
      fontSize: '1.5rem',
      color: '#FFFF00',
      textShadow: '2px 2px 0px #000',
      fontFamily: 'Press Start 2P, cursive',
      cursor: 'pointer',
      padding: '20px 40px',
      border: '4px solid #FFFF00',
      background: 'transparent',
      transition: 'all 0.2s ease',
      marginBottom: '40px'
    },
    copyright: {
      position: 'absolute',
      bottom: '40px',
      fontSize: '0.8rem',
      color: 'white',
      textShadow: '1px 1px 0px #000',
      fontFamily: 'Press Start 2P, cursive'
    },
    topHud: {
      position: 'absolute',
      top: '20px',
      width: '100%',
      display: 'flex',
      justifyContent: 'space-between',
      padding: '0 40px',
      fontSize: '1rem',
      color: 'white',
      textShadow: '2px 2px 0px #000',
      fontFamily: 'Press Start 2P, cursive'
    }
  };

  return (
    <div style={styles.container}>
      {/* Top HUD like original Mario */}
      <div style={styles.topHud}>
        <div>MARIO<br />000000</div>
        <div>🍄×00</div>
        <div>WORLD<br />1-1</div>
        <div>TIME</div>
      </div>

      {/* Background clouds */}
      <div style={styles.clouds}></div>

      {/* Main title box */}
      <div style={styles.titleBox}>
        <div style={styles.title}>SUPER</div>
        <div style={styles.subtitle}>MARIO BROS.</div>
      </div>

      {/* Start Button */}
      <button 
        style={styles.startButton}
        onClick={onStartGame}
      >
        <span style={styles.mario}>🍄</span> START GAME
      </button>

      <div style={{...styles.option, marginBottom: '20px'}}>
        TOP- 000000
      </div>

      {/* Copyright */}
      <div style={styles.copyright}>
        © 1985 NINTENDO
      </div>

      {/* Ground with pipes */}
      <div style={{
        position: 'absolute',
        bottom: 0,
        width: '100%',
        height: '120px',
        background: '#00AA00',
        borderTop: '8px solid #000'
      }}>
        {/* Simple ground pattern */}
        <div style={{
          width: '100%',
          height: '20px',
          background: 'repeating-linear-gradient(90deg, #00AA00 0px, #00AA00 40px, #008800 40px, #008800 80px)',
          marginTop: '10px'
        }}></div>
      </div>
    </div>
  );
};

export default StartScreen;
