import React, { useState } from 'react';
import StoryPanel from './StoryPanel';

const GameMap = ({ onEndGame }) => {
  const [completedStories, setCompletedStories] = useState([]);
  const [selectedStory, setSelectedStory] = useState(null);

  const storyItems = [
    {
      id: 1,
      position: { x: 200, y: 300 },
      icon: 'ðŸ„',
      title: 'The Beginning',
      text: "Welcome to the Mushroom Kingdom! Princess Peach needs your help to save the kingdom from Bowser's evil plans."
    },
    {
      id: 2,
      position: { x: 400, y: 250 },
      icon: 'â­',
      title: 'Power Star',
      text: "You found a Power Star! These magical stars will give you the strength to face greater challenges ahead."
    },
    {
      id: 3,
      position: { x: 600, y: 200 },
      icon: 'ðŸ°',
      title: 'Castle Mystery',
      text: "You've reached the first castle! But Princess Peach is in another castle. Keep going, Mario!"
    },
    {
      id: 4,
      position: { x: 800, y: 280 },
      icon: 'ðŸ”¥',
      title: 'Fire Power',
      text: "Watch out for the Fire Flowers! They can help you defeat enemies, but be careful not to get burned."
    },
    {
      id: 5,
      position: { x: 1000, y: 220 },
      icon: 'ðŸ‘‘',
      title: 'Victory!',
      text: "Congratulations! You've found Princess Peach! The kingdom is safe once again thanks to your bravery!"
    }
  ];

  const handleStoryClick = (story) => {
    setSelectedStory(story);
    if (!completedStories.includes(story.id)) {
      setCompletedStories([...completedStories, story.id]);
    }

    // End game when all stories are completed
    if (completedStories.length + 1 === storyItems.length) {
      setTimeout(() => onEndGame(), 2000);
    }
  };

  const closeStoryPanel = () => {
    setSelectedStory(null);
  };

  const styles = {
    container: {
      width: '100vw',
      height: '100vh',
      background: '#5C94FC',
      position: 'relative',
      overflow: 'hidden',
      fontFamily: 'Press Start 2P, cursive'
    },
    topHud: {
      position: 'fixed',
      top: '20px',
      width: '100%',
      display: 'flex',
      justifyContent: 'space-between',
      padding: '0 40px',
      fontSize: '1rem',
      color: 'white',
      textShadow: '2px 2px 0px #000',
      fontFamily: 'Press Start 2P, cursive',
      zIndex: 50
    },
    clouds: {
      position: 'absolute',
      width: '80px',
      height: '40px',
      background: 'white',
      borderRadius: '40px',
      opacity: 0.9
    },
    cloudBefore: {
      content: '',
      position: 'absolute',
      width: '40px',
      height: '40px',
      background: 'white',
      borderRadius: '50px',
      top: '-20px',
      left: '10px'
    },
    cloudAfter: {
      content: '',
      position: 'absolute',
      width: '50px',
      height: '30px',
      background: 'white',
      borderRadius: '30px',
      top: '-10px',
      right: '10px'
    },
    storyItem: {
      position: 'absolute',
      width: '32px',
      height: '32px',
      background: '#FFD700',
      border: '2px solid #000',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontSize: '20px',
      transition: 'all 0.1s ease',
      imageRendering: 'pixelated'
    },
    ground: {
      position: 'absolute',
      bottom: 0,
      width: '100%',
      height: '120px',
      background: '#D2691E',
      borderTop: '8px solid #000'
    },
    groundPattern: {
      width: '100%',
      height: '32px',
      background: 'repeating-linear-gradient(90deg, #D2691E 0px, #D2691E 32px, #8B4513 32px, #8B4513 64px)',
      marginTop: '8px'
    },
    pipe: {
      position: 'absolute',
      width: '64px',
      height: '96px',
      background: '#00AA00',
      border: '4px solid #000',
      bottom: '120px'
    },
    pipeTop: {
      position: 'absolute',
      top: '-8px',
      left: '-8px',
      width: '80px',
      height: '16px',
      background: '#00AA00',
      border: '4px solid #000'
    }
  };

  return (
    <>
      <style>
        {`
          @keyframes bounce {
            0%, 20%, 50%, 80%, 100% { transform: translateY(0); }
            40% { transform: translateY(-10px); }
            60% { transform: translateY(-5px); }
          }
        `}
      </style>
      <div style={styles.container}>
        <div style={styles.progressBar}>
          Stories Found: {completedStories.length}/{storyItems.length}
        </div>

        {storyItems.map(story => (
          <div
            key={story.id}
            style={{
              ...styles.storyItem,
              left: `${story.position.x}px`,
              top: `${story.position.y}px`,
              opacity: completedStories.includes(story.id) ? 0.6 : 1,
              transform: completedStories.includes(story.id) ? 'scale(0.8)' : 'scale(1)'
            }}
            onClick={() => handleStoryClick(story)}
            onMouseOver={(e) => e.target.style.transform = 'scale(1.1)'}
            onMouseOut={(e) => e.target.style.transform = completedStories.includes(story.id) ? 'scale(0.8)' : 'scale(1)'}
          >
            {story.icon}
          </div>
        ))}

        {selectedStory && (
          <StoryPanel 
            story={selectedStory} 
            onClose={closeStoryPanel} 
          />
        )}

        <div style={styles.ground}></div>
      </div>
    </>
  );
};

export default GameMap;
