import React from 'react';

const StartScreen = ({ onStartGame }) => {
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      background: '#5C94FC',
      textAlign: 'center',
      color: 'white',
      position: 'relative',
      overflow: 'hidden'
    },
    clouds: {
      position: 'absolute',
      width: '100%',
      height: '100%',
      backgroundImage: `
        radial-gradient(ellipse 60px 20px at 100px 50px, #FFFFFF 40%, transparent 50%),
        radial-gradient(ellipse 80px 30px at 300px 100px, #FFFFFF 40%, transparent 50%),
        radial-gradient(ellipse 100px 25px at 500px 80px, #FFFFFF 40%, transparent 50%)
      `,
      opacity: 0.8
    },
    titleBox: {
      background: '#FF6B00',
      border: '8px solid #000',
      borderRadius: '0',
      padding: '40px 60px',
      marginBottom: '40px',
      boxShadow: '8px 8px 0px #000'
    },
    title: {
      fontSize: '3rem',
      color: '#FFFF00',
      textShadow: '4px 4px 0px #000',
      marginBottom: '10px',
      fontFamily: 'Press Start 2P, cursive',
      letterSpacing: '4px'
    },
    subtitle: {
      fontSize: '1.5rem',
      color: '#FFFF00',
      textShadow: '2px 2px 0px #000',
      fontFamily: 'Press Start 2P, cursive',
      letterSpacing: '2px'
    },
    gameOptions: {
      display: 'flex',
      flexDirection: 'column',
      gap: '20px',
      marginBottom: '40px'
    },
    option: {
      fontSize: '1.2rem',
      color: 'white',
      textShadow: '2px 2px 0px #000',
      fontFamily: 'Press Start 2P, cursive',
      cursor: 'pointer',
      padding: '10px 20px',
      border: 'none',
      background: 'transparent',
      transition: 'all 0.2s ease'
    },
    selectedOption: {
      color: '#FFFF00'
    },
    mario: {
      fontSize: '2rem',
      marginRight: '20px'
    },
    copyright: {
      position: 'absolute',
      bottom: '40px',
      fontSize: '0.8rem',
      color: 'white',
      textShadow: '1px 1px 0px #000',
      fontFamily: 'Press Start 2P, cursive'
    },
    topHud: {
      position: 'absolute',
      top: '20px',
      width: '100%',
      display: 'flex',
      justifyContent: 'space-between',
      padding: '0 40px',
      fontSize: '1rem',
      color: 'white',
      textShadow: '2px 2px 0px #000',
      fontFamily: 'Press Start 2P, cursive'
    }
  };

  return (
    <>
      <style>
        {`
          @keyframes gradientShift {
            0% { background-position: 0% 50%; }
            50% { background-position: 100% 50%; }
            100% { background-position: 0% 50%; }
          }
        `}
      </style>
      <div style={styles.container}>
        <h1 style={styles.title}>üçÑ Princess Peach Adventure üçÑ</h1>
        <p style={styles.subtitle}>
          Help Mario save Princess Peach by exploring the kingdom!
        </p>
        <button 
          style={styles.button} 
          onClick={onStartGame}
          onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'}
          onMouseOut={(e) => e.target.style.transform = 'translateY(0)'}
        >
          Start Game
        </button>
        <p style={styles.instructions}>
          Click on story items to discover the adventure
        </p>
      </div>
    </>
  );
};

export default StartScreen;
