import React from 'react';

const EndScreen = ({ onRestartGame }) => {
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      background: '#000',
      textAlign: 'center',
      color: 'white',
      fontFamily: 'Press Start 2P, cursive',
      position: 'relative'
    },
    castle: {
      position: 'absolute',
      top: '20%',
      fontSize: '8rem',
      animation: 'glow 2s ease-in-out infinite alternate'
    },
    title: {
      fontSize: '2rem',
      marginBottom: '2rem',
      color: '#FFFF00',
      textShadow: '4px 4px 0px #000',
      letterSpacing: '3px'
    },
    subtitle: {
      fontSize: '1.2rem',
      marginBottom: '1rem',
      color: 'white'
    },
    message: {
      fontSize: '1rem',
      marginBottom: '3rem',
      color: '#00FF00',
      lineHeight: '1.8'
    },
    scoreBox: {
      border: '4px solid white',
      padding: '20px 40px',
      marginBottom: '2rem',
      background: '#000'
    },
    score: {
      fontSize: '1.5rem',
      color: '#FFFF00',
      marginBottom: '10px'
    },
    button: {
      padding: '15px 30px',
      fontSize: '1rem',
      background: 'transparent',
      color: '#FFFF00',
      border: '4px solid #FFFF00',
      cursor: 'pointer',
      fontFamily: 'Press Start 2P, cursive',
      transition: 'all 0.2s ease'
    },
    fireworks: {
      position: 'absolute',
      fontSize: '3rem',
      animation: 'sparkle 1.5s ease-in-out infinite'
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>ðŸŽ‰ Congratulations! ðŸŽ‰</h1>
      <p style={styles.subtitle}>
        You've completed the adventure!
      </p>
      <p style={styles.message}>
        Princess Peach and the Mushroom Kingdom are safe!
      </p>
      <button 
        style={styles.button} 
        onClick={onRestartGame}
        onMouseOver={(e) => {
          e.target.style.background = '#45B7D1';
          e.target.style.transform = 'translateY(-2px)';
        }}
        onMouseOut={(e) => {
          e.target.style.background = '#4ECDC4';
          e.target.style.transform = 'translateY(0)';
        }}
      >
        Play Again
      </button>
    </div>
  );
};

export default EndScreen;
