import React from 'react';

const StartScreen = ({ onStartGame }) => {
  const styles = {
    container: {
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      backgroundColor: '#87CEEB'
    },
    title: {
      fontSize: '3rem',
      color: '#fff',
      marginBottom: '2rem'
    },
    button: {
      fontSize: '1.5rem',
      padding: '1rem 2rem',
      backgroundColor: '#ff6b6b',
      color: 'white',
      border: 'none',
      borderRadius: '10px',
      cursor: 'pointer'
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>üçÑ Mario Game üçÑ</h1>
      <button style={styles.button} onClick={onStartGame}>
        Start Game
      </button>
    </div>
  );
};

export default StartScreen;
