import React from 'react';

const GameScreen = () => {
  const styles = {
    container: {
      width: '100vw',
      height: '100vh',
      overflowX: 'auto',
      overflowY: 'hidden',
      scrollBehavior: 'smooth'
    },
    scrollableContent: {
      width: '2000px',
      height: '100vh',
      backgroundImage: 'url(/backgrounds/tileable-background-skyline.png)',
      backgroundSize: 'auto 100%',
      backgroundPosition: 'left center',
      backgroundRepeat: 'repeat-x',
      position: 'relative'
    },
    bamboo: {
      position: 'absolute',
      left: '50px',
      bottom: '100px',
      width: '100px',
      height: 'auto',
      zIndex: 5
    },
    cherryBlossomsTop: {
      position: 'absolute',
      right: '0px',
      top: '30px',
      width: '200px',
      height: 'auto',
      zIndex: 5,
      animation: 'sway 3s ease-in-out infinite'
    },
    lantern: {
      position: 'absolute',
      left: '300px',
      top: '0px',
      width: '100px',
      height: 'auto',
      zIndex: 5,
      animation: 'sway 4s ease-in-out infinite reverse',
      filter: 'drop-shadow(0 0 8px rgba(255, 0, 0, 0.6))'
    },
    lantern2: {
      position: 'absolute',
      left: '150px',
      top: '0px',
      width: '80px',
      height: 'auto',
      zIndex: 5,
      animation: 'sway 3s ease-in-out infinite',
      filter: 'drop-shadow(0 0 8px rgba(255, 0, 0, 0.6))'
    },
    gameContent: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      textAlign: 'center',
      zIndex: 10
    },
    text: {
      fontSize: '3rem',
      color: '#fff',
      textShadow: '3px 3px 0px #000',
      fontFamily: 'monospace',
      marginBottom: '2rem'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.scrollableContent}>
        {/* Decorative elements that scroll with the background */}
        <img 
          src="/assets/sprites/bamboo.png" 
          alt="Bamboo" 
          style={styles.bamboo}
        />

        <img 
          src="/assets/sprites/lantern.png" 
          alt="Lantern" 
          style={styles.lantern}
        />
        <img 
          src="/assets/sprites/lantern.png" 
          alt="Lantern 2" 
          style={styles.lantern2}
        />

        <img 
          src="/assets/sprites/cherryblossom-sky.png" 
          alt="Cherry Blossoms Top" 
          style={styles.cherryBlossomsTop}
        />

        {/* Game content overlay */}
        <div style={styles.gameContent}>
          <h1 style={styles.text}>ðŸŽ® Game Screen! ðŸŽ®</h1>
          <p style={{...styles.text, fontSize: '1.5rem'}}>
            Scroll left and right to explore!
          </p>
        </div>
      </div>
    </div>
  );
};

export default GameScreen;
