import React from 'react';

const GameScreen = () => {
  const styles = {
    container: {
      width: '100vw',
      height: '100vh',
      position: 'relative',
      overflow: 'hidden'
    },
    sky: {
      position: 'absolute',
      top: 0,
      left: 0,
      width: '100%',
      height: '70%',
      backgroundImage: 'url(/assets/sprites/cherryblossom-sky.png)',
      backgroundRepeat: 'repeat',
      backgroundSize: 'auto'
    },
    ground: {
      position: 'absolute',
      bottom: 0,
      left: 0,
      width: '100%',
      height: '30%',
      backgroundImage: 'url(/assets/sprites/cherryblossom-floor.png)',
      backgroundRepeat: 'repeat-x',
      backgroundSize: 'auto 100%'
    },
    gameContent: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      textAlign: 'center',
      zIndex: 10
    },
    text: {
      fontSize: '3rem',
      color: '#fff',
      textShadow: '3px 3px 0px #000',
      fontFamily: 'monospace',
      marginBottom: '2rem'
    }
  };

  return (
    <div style={styles.container}>
      {/* Tileable sky background */}
      <div style={styles.sky}></div>

      {/* Tileable ground background */}
      <div style={styles.ground}></div>

      {/* Game content overlay */}
      <div style={styles.gameContent}>
        <h1 style={styles.text}>ðŸŽ® Game Screen! ðŸŽ®</h1>
        <p style={{...styles.text, fontSize: '1.5rem'}}>
          Ready to play!
        </p>
      </div>
    </div>
  );
};

export default GameScreen;
